import React, { useState } from "react";
import CameraCapture from "./components/CameraCapture";
import ImageUpload from "./components/ImageUpload";
import ResultCard from "./components/ResultCard";
import Header from "./components/Header";
import Footer from "./components/Footer";

export default function App(){
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const analyzeFile = async (file) => {
    setLoading(true);
    setResult(null);
    try {
      const form = new FormData();
      form.append("image", file);

      // Backend URL - change if hosted elsewhere
      const resp = await fetch(process.env.REACT_APP_API_URL || "http://localhost:5000/analyze", {
        method: "POST",
        body: form
      });
      const data = await resp.json();
      setResult(data);
    } catch (e){
      console.error(e);
      setResult({ error: e.message || "Error analyzing image" });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-6">
      <div className="w-full max-w-3xl bg-white rounded-2xl shadow-lg p-6">
        <h1 className="text-2xl font-bold mb-2">Image Forensics — Real or Fake</h1>
        <p className="text-sm text-gray-500 mb-6">Upload an image or use your camera. GPT verdict = “Fake/Synthetic” when ensemble score &gt; 0.5.</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <CameraCapture onCapture={analyzeFile} />
          <ImageUpload onUpload={analyzeFile} />
        </div>

        <div className="mt-6">
          {loading && <div className="text-center py-6">Analyzing... </div>}
          {result && <ResultCard result={result} />}
        </div>
      </div>
    </div>
  );
}
